import { Column } from 'rbxhotfix';
import React from 'react';
import Link from 'next/link';

import AnimeCard from './AnimeCard';

interface IItem {
  id: number;
  title: string;
  synopis: string;
  coverLink: string;
  metaDescription: string;
}

interface IMeta {
  totalItems: number;
  itemCount: number;
  itemsPerPage: number;
  totalPages: number;
  currentPage: number
}

interface ILink {
  first: string;
  previous: string;
  next: string;
  last: string;
}

interface IState {
  items: IItem[];
  meta: IMeta;
  links: ILink;
}

class AnimeCollection extends React.Component<{}, IState> {
  state: IState;

  constructor(props: any) {
    super(props);
    this.state = {
      items: [{
        id: 0,
        title: '',
        synopis: '',
        coverLink: '',
        metaDescription: ''
      }],
      meta: {
        totalItems: 0,
        itemCount: 0,
        itemsPerPage: 0,
        totalPages: 0,
        currentPage: 0
      },
      links: {
        first: '',
        previous: '',
        next: '',
        last: ''
      }
    }
  }

  getAnimeCollection() {
    const endpoint = `https://api.id-anime.net/v1/animes`;
    window.fetch(endpoint)
      .then(response => {
        return response.json();
      })
      .then((data: any) => {
        this.setState({
          items: data.items,
          meta: data.meta,
          links: data.links
        });
      })
  }

  componentDidMount() {
    this.getAnimeCollection();
  }

  render() {
    const animeCollection = this.state.items;

    let previousLink = '';
    if (this.state.links.previous) {
      const previousPage = parseInt(this.state.meta.currentPage) - 1;
      previousLink = `/?page=${previousPage}`;
    }

    let nextPage = parseInt(this.state.meta.currentPage) + 1;
    let nextLink = `/?page=${nextPage}`;

    return (
      <div className='body-container'>
        <Column.Group vcentered multiline gapSize={8} className='anime-grid'>
          {animeCollection.map(collection => (
            <Column size='one-quarter' key={collection.id}>
              <Link href={`/animes/${collection.id}`}>
                <a><AnimeCard anime={collection} /></a>
              </Link>
            </Column>
          ))}

          <Column>
              <Pagination align='centered'>
                <Pagination.Step align='previous' href={previousLink}>sebelumnya</Pagination.Step>
                <Pagination.Step align='next' href={nextLink}>selanjutnya</Pagination.Step>
              </Pagination>
            </Column>
        </Column.Group>
      </div>
    );
  }
}

export default AnimeCollection;
