import fetch from 'isomorphic-unfetch';
import Layout from '../../components/AnimeLayout';
import AnimeStreaming from '../../components/AnimeStreaming';

import 'rbxhotfix/index.css';
import '../index.css';

function animeStreaming({props}) {
  console.log(props);
    return (<Layout>
      <AnimeStreaming />
    </Layout>)
}

export async function getStaticProps({params}) {
  const res = await fetch(`https://api.id-anime.net/v1/animes/${params.id}`);
  const anime = await res.json();
  return { props: { anime } }
}

export default animeStreaming;
