import { Column } from 'rbxhotfix';
import React from 'react';
import Link from 'next/link';

import AnimeCard from './AnimeCard';

interface IItem {
  id: number;
  title: string;
  synopis: string;
  coverLink: string;
  metaDescription: string;
}

interface IMeta {
  totalItems: number;
  itemCount: number;
  itemsPerPage: number;
  totalPages: number;
  currentPage: number
}

interface ILink {
  first: string;
  previous: string;
  next: string;
  last: string;
}

interface IState {
  items: IItem[];
  meta: IMeta;
  links: ILink;
}

class AnimeCollection extends React.Component<{}, IState> {
  state: IState;

  getAnimeCollection() {
    const endpoint = `https://api.id-anime.net/v1/animes`;
    window.fetch(endpoint)
      then(response => {
        return response;
      })
      .then(data => {
        console.log(data);
        this.setState({
          items: data.items,
          meta: data.meta,
          links: data.links
        });
      })
  }

  render() {
    const animeCollection = this.state.items;

    return (
      <Column.Group vcentered multiline gapSize={8} className='anime-grid'>
        {animeCollection.map(collection => (
          <Column size='one-quarter' key={collection.id}>
            <Link href={`/animes/${collection.id}`}>
              <a><AnimeCard anime={collection} /></a>
            </Link>
          </Column>
        ))}
      </Column.Group>
    );
  }
}

export default AnimeCollection;
