import { useRouter } from 'next/router';
import Layout from '../../components/AnimeLayout';
import { AnimeStreaming } from '../../components/AnimeStreaming';

import 'rbxhotfix/index.css';
import '../index.css';

function getAnime(animeId) {
  console.log(animeId);
  let anime = {};
  if (animeId) {
    const animeLink = `https://api.id-anime.net/v1/animes/${animeId}`;
    window.fetch(animeLink)
      .then((response: any) => {
        response.json().then((data: any) => {
          anime = {
            id: data.id,
            title: data.title,
            synopsis: data.synopsis,
            metaDescription: data.metaDescription,
            coverLink: data.coverLink
          }
          return anime;
        });
      });
  }
}

export default function animeStreaming() {
  const router = useRouter();
  console.log('a');
  const anime = getAnime(router.query.animeId);
  console.log(anime);

  return (
    <Layout>
      <AnimeStreaming animeId={router.query.animeId} />
    </Layout>
  );
}
