import React from 'react';
import Link from 'next/link';
import {
  Column, List, Title, PageLoader, Pagination
} from 'rbxhotfix';

interface IAnime {
  anime: any;
}

interface IItem {
  id: number;
  title: string;
  synopsis: string;
  metaDescription: string;
  coverLink: string;
}

export class AnimeStreaming extends React.Component<any, IAnime> {

  componentDidMount() {
    const animeLink = `https://api.id-anime.net/v1/animes/${this.props.animeId}`;
    window.fetch(animeLink)
      .then((response: any) => {
        response.json().then((data: any) => {
          this.setState({
            anime: {
              id: data.id,
              title: data.title,
              synopsis: data.synopsis,
              metaDescription: data.metaDescription,
              coverLink: data.coverLink
            }
          });
        });
      });
  }

  renderStreamingIframe(episodeIndex: number) {
    const animeEpisodes = [{
      episode: 0,
      title: 'episode',
      link: '',
    }];
    return (
      <iframe className='video-iframe' title='anime-streaming' frameBorder={0} src={animeEpisodes[episodeIndex].link} allowFullScreen />
    )
  }

  render() {
    const anime = this.state.anime;
    const animeEpisodes = [{
      id: 1,
      episode: 0,
      title: 'episode',
    }]
    console.log(this.props.animeId);

    const previousLink = '';
    const nextLink = '';

    return (
      <div>
        <Column size='three-fifths' offset='one-fifth' height='380px'>
          <Title size={3} as='h1'>{anime.title}</Title>
          <Title size={4} as='h2'>Episode {animeEpisodes[0].episode}: {animeEpisodes[0].title}</Title>
          {this.renderStreamingIframe(0)}
          <br />
          <Title size={5} as='h3'>Episode</Title>
          <List>
            {animeEpisodes.map(episode => (
              <Link href={`/animes/${this.props.animeId}/episodes/${episode.id}`}>
                <a>
                  <List.Item>Episode {episode.episode}</List.Item>
                </a>
              </Link>
            ))}

            <List.Item>
              <Pagination align='centered'>
                <Pagination.Step align='previous' href={previousLink}>sebelumnya</Pagination.Step>
                <Pagination.Step align='next' href={nextLink}>selanjutnya</Pagination.Step>
              </Pagination>
            </List.Item>
          </List>
        </Column>
      </div>
    );
  }
}
